 protected void datatable() {
        Object[] Baris ={"id_barang","nama_barang","merek","jenis","harga","stok","tanggal_k"};
        tabmode = new DefaultTableModel(null, Baris);
        tabel.setModel(tabmode);
        String sql = "select * from barang "; 
        try {
            java.sql.Statement stat = kon.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            while(hasil.next()){
                String a = hasil.getString("id_barang");
                String b = hasil.getString("nama_barang");
                String c = hasil.getString("merek");
                String d = hasil.getString("jenis");
                 String e = hasil.getString("harga");
                String f = hasil.getString("stok");
                  String g = hasil.getString("tanggal_k");
            
                String[] data={a,b,c,d,e,f,g};
                tabmode.addRow(data);
            }
        } catch (Exception e){}
    }
 
 protected void cari(){
        Object[] Baris ={"id_barang","nama_barang","merek","jenis","harga","stok","tanggal_k"};
        tabmode = new DefaultTableModel(null, Baris);
        tabel.setModel(tabmode);
        try {
        String sql = "select * from barang"
                + " WHERE nama_barang LIKE '%" + brs.getText() + "%' OR nama_barang LIKE '" + brs.getText() + "%' OR nama_barang LIKE '%" + brs.getText() + "'"
                + " ORDER BY id_barang ASC";
            PreparedStatement pst = kon.prepareStatement(sql);
//            java.sql.Statement stat = conn.createStatement();
            ResultSet hasil = pst.executeQuery(sql);
            while(hasil.next()){
              String a = hasil.getString("id_barang");
                String b = hasil.getString("nama_barang");
                String c = hasil.getString("merek");
                String d = hasil.getString("jenis");
                 String e = hasil.getString("harga");
                String f = hasil.getString("stok");
                  String g = hasil.getString("tanggal_k");
            
                String[] data={a,b,c,d,e,f,g};
                tabmode.addRow(data);
            }
        } catch (Exception e){
        JOptionPane.showMessageDialog(null,e);}
    }

 
 
