    private void bEdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bEdActionPerformed
        try{
            String sql = "update barang set nama_barang=?, merek=?, jenis=?, harga=?, stok=?, tanggal_k=? where id_barang=?";
            PreparedStatement stat = kon.prepareStatement(sql);

            stat.setString(1, jNm.getText());
            stat.setString(2, txtM.getText());
            stat.setString(3, tjn.getText());
            stat.setString(4, jH.getText());
            stat.setString(5, jSt.getText());
             java.util.Date utilStartDate = tgl.getDate();
        java.sql.Date sqlStartDate = new java.sql.Date(utilStartDate.getTime());
        stat.setDate(6, sqlStartDate);
         stat.setString(7, jId.getText());
        
            stat.executeUpdate();
            kosong();
            datatable();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diubah");

        }catch (SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Diubah"+e);
        }
    }//GEN-LAST:event_bEdActionPerformed

    private void bThActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bThActionPerformed
        String sql = "insert into barang (id_barang,nama_barang,merek,jenis,harga,stok,tanggal_k) values (?,?,?,?,?,?,?)";
        try {
            PreparedStatement stat = kon.prepareStatement(sql);

            stat.setString(1, jId.getText());
                stat.setString(2, jNm.getText());
            stat.setString(3, txtM.getText());
            stat.setString(4, tjn.getText());
             stat.setString(5, jSt.getText());
            stat.setString(6, jH.getText());
           
             java.util.Date utilStartDate = tgl.getDate();
        java.sql.Date sqlStartDate = new java.sql.Date(utilStartDate.getTime());
        stat.setDate(7, sqlStartDate);

            stat.executeUpdate();
            kosong();
            
            datatable();
            JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");

        }catch (SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan"+e);
        }
    }//GEN-LAST:event_bThActionPerformed

